import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom"; // Cambiado de useHistory a useNavigate

function Home() {
  const navigate = useNavigate(); // useNavigate en lugar de useHistory

  // Verifica si el token est치 en localStorage
  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      // Si no hay token, redirige al login
      navigate("/signin"); // Usamos navigate para redirigir
    }
  }, [navigate]); // A침adido navigate a las dependencias

  return (
    <div>
      <h3>SPS REACT TEST</h3>
        <div class="row justify-content-center">
          <div class="col-lg-6 col-md-6 col-sm-6">
              <div class="card m-1 bg-dark text-white border border-0 shadow">
                <div class="card-body">
                  <div class="row">
                    <div class="col-9 col-md-9 col-sm-9">
                      <a href="/users"><p class="card-subtitle">Usuarios</p></a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
         </div>
    </div>
  );
}

export default Home;
