import axios from "axios";

class UserService {
  constructor() {
    this.baseURL = 'http://localhost:3000/users';
    this.token = localStorage.getItem('token') || '';
    this.header = {
      headers: {
        'authorization': `Bearer ${this.token}`,
        'Content-Type': 'application/json',
        'Accept': 'application/json', 
      },
    };
  }
  async list() {
    try {
      const { data } = await axios.get(this.baseURL, this.header);
      console.log(data);
      return data.data;  // Regresa la lista de usuarios
    } catch (error) {
      console.error(error);
      if (error.response && (error.response.status === 403 || error.response.status === 401)) {
        this.expirado();
      }
      throw error; // Lanza el error para manejarlo en otros lugares
    }
  }
  async get(id) {
    throw new Error("Not implemented");
  }
  async create(userData) {
    try {
      const { data } = await axios.post(`${this.baseURL}/store`, userData, this.header);
      console.log(data);
      return data;
    } catch (error) {
      console.error(error);
      if (error.response && (error.response.status === 403 || error.response.status === 401)) {
        this.expirado();
      }
      throw error; // Lanza el error para manejarlo en otros lugares
    }
  }
  async delete(id) {
    throw new Error("Not implemented");
  }
  async update(id, data) {
    throw new Error("Not implemented");
  }
}

export default new UserService;
