import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Importa useNavigate
import axios from "axios";
import Swal from 'sweetalert2';

function SignIn() {
  const [correo, setCorreo] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Usa useNavigate

  const ingresar = async () => {
    if (correo === "" || password === "") {
      Swal.fire({
        title: 'Error!',
        text: 'Ingrese sus credenciales!!',
        icon: 'error',
      });
      return;
    }

    try {
      const response = await axios.post("http://localhost:3000/auth/login", {
        correo,
        password,
      });
      localStorage.setItem("token", response.data.token);
      alert("Bienvenido!");

      Swal.fire({
        title: "Bienvenido!",
        text: "Estas de regreso!!",
        icon: "success",
        showConfirmButton: false,
        timer: 1000
      });


      // Redirigir a la página principal o la página que desees
      navigate("/"); // Usa navigate en lugar de history.push
    } catch (error) {
      alert("Error al iniciar sesión");
    }
  };

  return (
     <div class="d-flex justify-content-center">
     <div class="card" >
         <div class="card-body">
             <h2 class="text-center">Iniciar sesión</h2>
             <h3 class="text-center">NUR</h3>
             <div class="mb-3">
                 <label for="correo" class="form-label">Correo</label>
                 <input type="email" placeholder ="Correo" value={correo} class="form-control" onChange={(e) => setCorreo(e.target.value)} />
             </div>

             <div class="mb-3">
                 <label for="password" class="form-label">Contraseña</label>
                 <input type="password" placeholder="Contraseña" value={password} onChange={(e) => setPassword(e.target.value)} class="form-control" />
             </div>

             <div class="mb-3 text-center">
                 <button onclick={ingresar} class="btn btn-primary">Iniciar sesión</button>
             </div>

         </div>
     </div>
    </div>
  );
}

export default SignIn;
